SELECT products.product_id, products.product_name, stocks.quantity from stocks INNER JOIN products ON stocks.product_id=products.product_id INNER JOIN categories ON categories.category_id=products.category_id INNER JOIN stores ON stores.store_id=stocks.store_id WHERE category_name='Electric Bikes' AND store_name='Santa Cruz Bikes' GROUP BY products.product_id, stocks.quantity ORDER BY product_name;